example.code:
using Syncfusion.Windows.Diagram;
namespace WpfApplication1
{
public partial class Window1 : Window
{
public DiagramControl Control;
public DiagramModel Model;
public DiagramView View;
public Window1 ()
{
InitializeComponent ();
Control = new DiagramControl ();
Model = new DiagramModel ();
View = new DiagramView ();
Control.View = View;
Control.Model = Model;
View.Bounds = new Thickness(0, 0, 1000, 1000);
//Creating node
Node n = new Node(Guid.NewGuid(), "Start");
n.Shape = Shapes.FlowChart_Start;
n.IsLabelEditable = true;
n.Label = "Start";
n.Level = 1;
n.OffsetX = 150;
n.OffsetY = 25;
n.Width = 150;
n.Height = 75;
n.ToolTip="Start Node";
Model.Nodes.Add(n);
Node n1 = new Node(Guid.NewGuid(), "Decision1");
n1.Shape = Shapes.FlowChart_Process;
n1.IsLabelEditable = true;
n1.Label = "Alarm Rings";
n1.Level = 2;
n1.OffsetX = 150;
n1.OffsetY = 125;
n1.Width = 150;
n1.Height = 75;
Model.Nodes.Add(n1);
//Creating a connection.
LineConnector o2 = new LineConnector();
o2.ConnectorType = ConnectorType.Straight;
o2.TailNode = n1;
o2.HeadNode = n;
IShape node = o2.AdjacentNode(n1);
Model.Connections.Add(o2);
}
}
}

using Syncfusion.Windows.Diagram;
namespace WpfApplication1
{
public partial class Window1 : Window
{
   public DiagramControl Control;
   public DiagramModel Model;
   public DiagramView View;
   public Window1 ()
   {
      InitializeComponent ();
      Control = new DiagramControl ();
      Model = new DiagramModel ();
      View = new DiagramView ();
      Control.View = View;
      Control.Model = Model;
      View.Bounds = new Thickness(0, 0, 1000, 1000);
      //Specifies the node
       Node n = new Node(Guid.NewGuid(), "Start");
       n.Shape = Shapes.FlowChart_Start;
       n.IsLabelEditable = true;
       n.Label = "Start";
       n.Level = 1;
       n.OffsetX = 150;
       n.OffsetY = 25;
       n.Width = 150;
       n.Height = 75;
       n.ToolTip="Start Node";
       Model.Nodes.Add(n);
        Node n1 = new Node(Guid.NewGuid(), "Decision1");
        n1.Shape = Shapes.FlowChart_Process;
        n1.IsLabelEditable = true;
        n1.Label = "Alarm Rings";
        n1.Level = 2;
        n1.OffsetX = 150;
        n1.OffsetY = 125;
        n1.Width = 150;
        n1.Height = 75;
       Model.Nodes.Add(n1);
        LineConnector o = new LineConnector();
        o.ConnectorType = ConnectorType.Straight;
        o.TailNode = n1;
        o.HeadNode = n;
        o.StartPointPosition=new Point(100,100);
        o.EndPointPosition=new Point(200,200);
        Model.Connections.Add(o);
   }
   }
   }

