This example shows how to set IsLabelEditable in C#.

            using Syncfusion.Windows.Diagram;
            namespace WpfApplication1
            {
            public partial class Window1 : Window
            {
            public DiagramControl Control;
            public DiagramModel Model;
            public DiagramView View;
            public Window1 ()
            {
            InitializeComponent ();
            Control = new DiagramControl ();
            Model = new DiagramModel ();
            View = new DiagramView ();
            Control.View = View;
            Control.Model = Model;
            View.Bounds = new Thickness(0, 0, 1000, 1000);
            Node n = new Node(Guid.NewGuid(), "Start");
            n.Shape = Shapes.FlowChart_Start;
            n.OffsetX = 150;
            n.OffsetY = 25;
            n.Width = 150;
            n.Height = 75;
            Model.Nodes.Add(n);
            Node n1 = new Node(Guid.NewGuid(), "Decision1");
            n1.Shape = Shapes.FlowChart_Process;
            n1.Label = "Alarm Rings";
            n1.OffsetX = 150;
            n1.OffsetY = 125;
            n1.Width = 150;
            n1.Height = 75;
            Model.Nodes.Add(n1);
            LineConnector connObject = new LineConnector();
            connObject.ConnectorType = ConnectorType.Straight;
            connObject.TailNode = n1;
            connObject.HeadNode = n;
            connObject.ConnectorType = ConnectorType.Orthogonal;
            connObject.LabelVisibility = Visibility.Visible;
            connObject.IsLabelEitable = true;
            Model.Connections.Add(connObject);
            }
            }
            }


This example shows how to set  LabelTemplate   in C#.

            using Syncfusion.Windows.Diagram;
            namespace WpfApplication1
            {
            public partial class Window1 : Window
            {
            public DiagramControl Control;
            public DiagramModel Model;
            public DiagramView View;
            public Window1 ()
            {
            InitializeComponent ();
            Control = new DiagramControl ();
            Model = new DiagramModel ();
            View = new DiagramView ();
            Control.View = View;
            Control.Model = Model;
            View.Bounds = new Thickness(0, 0, 1000, 1000);
            Node n = new Node(Guid.NewGuid(), "Start");
            n.Shape = Shapes.FlowChart_Start;
            n.OffsetX = 150;
            n.OffsetY = 25;
            n.Width = 150;
            n.Height = 75;
            Model.Nodes.Add(n);
            Node n1 = new Node(Guid.NewGuid(), "Decision1");
            n1.Shape = Shapes.FlowChart_Process;
            n1.Label = "Alarm Rings";
            n1.OffsetX = 150;
            n1.OffsetY = 125;
            n1.Width = 150;
            n1.Height = 75;
            Model.Nodes.Add(n1);
            LineConnector connObject = new LineConnector();
            connObject.ConnectorType = ConnectorType.Straight;
            connObject.TailNode = n1;
            connObject.HeadNode = n;
            connObject.ConnectorType = ConnectorType.Orthogonal;
            connObject.LabelTemplate = (ControlTemplate)FindResource( "LabelCustomTemplate" );
            Model.Connections.Add(connObject);
            }
            }
            }


This example shows how to write a  LabelTemplate in XAML.

            <ControlTemplate x:Key="LabelCustomTemplate">
            <StackPanel Orientation="Horizontal">
            <Image Source="text.png" Width="20" Height="20"/>
            <TextBlock Text="Hello"/>
            </StackPanel>
            </ControlTemplate>


This example shows how to set  LabelTemplate   in C#.

            using Syncfusion.Windows.Diagram;
            namespace WpfApplication1
            {
            public partial class Window1 : Window
            {
            public DiagramControl Control;
            public DiagramModel Model;
            public DiagramView View;
            public Window1 ()
            {
            InitializeComponent ();
            Control = new DiagramControl ();
            Model = new DiagramModel ();
            View = new DiagramView ();
            Control.View = View;
            Control.Model = Model;
            View.Bounds = new Thickness(0, 0, 1000, 1000);
            Node n = new Node(Guid.NewGuid(), "Start");
            n.Shape = Shapes.FlowChart_Start;
            n.OffsetX = 150;
            n.OffsetY = 25;
            n.Width = 150;
            n.Height = 75;
            Model.Nodes.Add(n);
            Node n1 = new Node(Guid.NewGuid(), "Decision1");
            n1.Shape = Shapes.FlowChart_Process;
            n1.Label = "Alarm Rings";
            n1.OffsetX = 150;
            n1.OffsetY = 125;
            n1.Width = 150;
            n1.Height = 75;
            Model.Nodes.Add(n1);
            LineConnector connObject = new LineConnector();
            connObject.ConnectorType = ConnectorType.Straight;
            connObject.TailNode = n1;
            connObject.HeadNode = n;
            connObject.ConnectorType = ConnectorType.Orthogonal;
            connObject.IsSelected = true;
            Model.Connections.Add(connObject);
            }
            }
            }


This example shows how to set LineStyle in C#.

            using Syncfusion.Windows.Diagram;
            namespace WpfApplication1
            {
            public partial class Window1 : Window
            {
            public DiagramControl Control;
            public DiagramModel Model;
            public DiagramView View;
            public Window1 ()
            {
            InitializeComponent ();
            Control = new DiagramControl ();
            Model = new DiagramModel ();
            View = new DiagramView ();
            Control.View = View;
            Control.Model = Model;
            View.Bounds = new Thickness(0, 0, 1000, 1000);
            Node n = new Node(Guid.NewGuid(), "Start");
            n.Shape = Shapes.FlowChart_Start;
            n.OffsetX = 150;
            n.OffsetY = 25;
            n.Width = 150;
            n.Height = 75;
            Model.Nodes.Add(n);
            Node n1 = new Node(Guid.NewGuid(), "Decision1");
            n1.Shape = Shapes.FlowChart_Process;
            n1.Label = "Alarm Rings";
            n1.OffsetX = 150;
            n1.OffsetY = 125;
            n1.Width = 150;
            n1.Height = 75;
            Model.Nodes.Add(n1);
            LineConnector connObject = new LineConnector();
            connObject.ConnectorType = ConnectorType.Straight;
            connObject.TailNode = n1;
            connObject.HeadNode = n;
            connObject.ConnectorType = ConnectorType.Orthogonal;
            connObject.LineStyle.Fill = Brushes.Red;
            Model.Connections.Add(connObject);
            }
            }
            }


This example shows how to set HeadDecoratorStyle in C#.

            using Syncfusion.Windows.Diagram;
            namespace WpfApplication1
            {
            public partial class Window1 : Window
            {
            public DiagramControl Control;
            public DiagramModel Model;
            public DiagramView View;
            public Window1 ()
            {
            InitializeComponent ();
            Control = new DiagramControl ();
            Model = new DiagramModel ();
            View = new DiagramView ();
            Control.View = View;
            Control.Model = Model;
            View.Bounds = new Thickness(0, 0, 1000, 1000);
            Node n = new Node(Guid.NewGuid(), "Start");
            n.Shape = Shapes.FlowChart_Start;
            n.OffsetX = 150;
            n.OffsetY = 25;
            n.Width = 150;
            n.Height = 75;
            Model.Nodes.Add(n);
            Node n1 = new Node(Guid.NewGuid(), "Decision1");
            n1.Shape = Shapes.FlowChart_Process;
            n1.Label = "Alarm Rings";
            n1.OffsetX = 150;
            n1.OffsetY = 125;
            n1.Width = 150;
            n1.Height = 75;
            Model.Nodes.Add(n1);
            LineConnector connObject = new LineConnector();
            connObject.ConnectorType = ConnectorType.Straight;
            connObject.TailNode = n1;
            connObject.HeadNode = n;
            connObject.ConnectorType = ConnectorType.Orthogonal;
            connObject.LineStyle.Fill = Brushes.Red;
            connObject.HeadDecoratorStyle.Fill = Brushes.Orange; 
            Model.Connections.Add(connObject);
            }
            }
            }


This example shows how to set TailDecoratorStyle in C#.

            using Syncfusion.Windows.Diagram;
            namespace WpfApplication1
            {
            public partial class Window1 : Window
            {
            public DiagramControl Control;
            public DiagramModel Model;
            public DiagramView View;
            public Window1 ()
            {
            InitializeComponent ();
            Control = new DiagramControl ();
            Model = new DiagramModel ();
            View = new DiagramView ();
            Control.View = View;
            Control.Model = Model;
            View.Bounds = new Thickness(0, 0, 1000, 1000);
            Node n = new Node(Guid.NewGuid(), "Start");
            n.Shape = Shapes.FlowChart_Start;
            n.OffsetX = 150;
            n.OffsetY = 25;
            n.Width = 150;
            n.Height = 75;
            Model.Nodes.Add(n);
            Node n1 = new Node(Guid.NewGuid(), "Decision1");
            n1.Shape = Shapes.FlowChart_Process;
            n1.Label = "Alarm Rings";
            n1.OffsetX = 150;
            n1.OffsetY = 125;
            n1.Width = 150;
            n1.Height = 75;
            Model.Nodes.Add(n1);
            LineConnector connObject = new LineConnector();
            connObject.ConnectorType = ConnectorType.Straight;
            connObject.TailNode = n1;
            connObject.HeadNode = n;
            connObject.ConnectorType = ConnectorType.Orthogonal;
            connObject.LineStyle.Fill = Brushes.Red;
            connObject.TailDecoratorStyle.Fill = Brushes.Orange; 
            Model.Connections.Add(connObject);
            }
            }
            }


