n.IsLabelEditable = true;
n.Label = "Start";
n.OffsetX = 150;
n.OffsetY = 25;
n.Width = 150;
n.Height = 75;
n.ToolTip="Start Node";
n.GripperVisibility=Visibility.Visible;
Model.Nodes.Add(n);
}
}
