member.example:

This example shows how to set PathStyle of a node .
            Specify a resource in XAML .

            <Style TargetType="{x:Type Path}" x:Key="myNode">
                <Setter Property="Data" Value="M200,239L200,200 240,239 280,202 320,238 281,279 240,244 198,279z"></Setter>
                <Setter Property="Fill" Value="MidnightBlue" />
            </Style>


            using Syncfusion.Core;
            using Syncfusion.Windows.Diagram;
            namespace WpfApplication1
            {
            public partial class Window1 : Window
            {
               public DiagramControl Control;
               public DiagramModel Model;
               public DiagramView View;
               public Window1 ()
               {
                  InitializeComponent ();
                  Control = new DiagramControl ();
                  Model = new DiagramModel ();
                  View = new DiagramView ();
                  Control.View = View;
                  Control.Model = Model;
                  View.Bounds = new Thickness(0, 0, 1000, 1000);
                  Node n = new Node(Guid.NewGuid(), "Start");
                  Style customstyle = (Style)this.Resources["myNode"];
                   n.CustomPathStyle=customstyle;
                   n.Shape = Shapes.Custom;
                   n.IsLabelEditable = true;
                   n.Label = "Start";
                   n.OffsetX = 150;
                   n.OffsetY = 25;
                   n.Width = 150;
                   n.Height = 75;
                   Model.Nodes.Add(n);
               }
               }
               }

